    /*
     * set up varbinds
     */
    memset( &var_powerIndex, 0x00, sizeof(var_powerIndex) );
    var_powerIndex.type = ASN_OCTET_STR;

    /*
     * chain temp index varbinds together
     */
    var_powerIndex.next_variable =  NULL;


    DEBUGMSGTL(("verbose:powerTable:powerTable_index_to_oid","called\n"));

        /* powerIndex(1)/OCTETSTR/ASN_OCTET_STR/char(char)//L/a/w/e/R/d/h */
    snmp_set_var_value(&var_powerIndex, &mib_idx->powerIndex,
                       mib_idx->powerIndex_len * sizeof(mib_idx->powerIndex[0]));


    err = build_oid_noalloc(oid_idx->oids, oid_idx->len, &oid_idx->len,
                           NULL, 0, &var_powerIndex);
    if(err)
        snmp_log(LOG_ERR,"error %d converting index to oid\n", err);

    /*
     * parsing may have allocated memory. free it.
     */
    snmp_reset_var_buffers( &var_powerIndex );

