/**
 * extract powerTable indexes from a netsnmp_index
 *
 * @retval SNMP_ERR_NOERROR  : no error
 * @retval SNMP_ERR_GENERR   : error
 */
int
powerTable_index_from_oid(netsnmp_index *oid_idx,
                         powerTable_mib_index *mib_idx)
{
    int err = SNMP_ERR_NOERROR;
    
    /*
     * temp storage for parsing indexes
     */
    /*
     * powerIndex(1)/OCTETSTR/ASN_OCTET_STR/char(char)//L/a/w/e/R/d/h
     */
    netsnmp_variable_list var_powerIndex;

    /*
     * set up varbinds
     */
    memset( &var_powerIndex, 0x00, sizeof(var_powerIndex) );
    var_powerIndex.type = ASN_OCTET_STR;

    /*
     * chain temp index varbinds together
     */
    var_powerIndex.next_variable =  NULL;


    DEBUGMSGTL(("verbose:powerTable:powerTable_index_from_oid","called\n"));

    /*
     * parse the oid into the individual index components
     */
    err = parse_oid_indexes( oid_idx->oids, oid_idx->len,
                             &var_powerIndex );
    if (err == SNMP_ERR_NOERROR) {
        /*
         * copy out values
         */
    /*
     * NOTE: val_len is in bytes, powerIndex_len might not be
     */
         if(var_powerIndex.val_len > sizeof(mib_idx->powerIndex))
             err = SNMP_ERR_GENERR;
         else {
             memcpy(mib_idx->powerIndex, var_powerIndex.val.string, var_powerIndex.val_len);
             mib_idx->powerIndex_len = var_powerIndex.val_len / sizeof(mib_idx->powerIndex[0]);
         }


    }

    /*
     * parsing may have allocated memory. free it.
     */
    snmp_reset_var_buffers( &var_powerIndex );

    return err;
} /* powerTable_index_from_oid */


