/* *********************************************************************
 * @internal
 * allocate resources for a powerTable_rowreq_ctx
 */
powerTable_rowreq_ctx *
powerTable_allocate_rowreq_ctx(void *user_init_ctx)
{
    powerTable_rowreq_ctx *rowreq_ctx =
                  SNMP_MALLOC_TYPEDEF(powerTable_rowreq_ctx);

    DEBUGMSGTL(("internal:powerTable:powerTable_allocate_rowreq_ctx","called\n"));

    if(NULL == rowreq_ctx) {
        snmp_log(LOG_ERR,"Couldn't allocate memory for a "
                 "powerTable_rowreq_ctx.\n");
        return NULL;
    }

    rowreq_ctx->oid_idx.oids = rowreq_ctx->oid_tmp;

    rowreq_ctx->powerTable_data_list = NULL;

    /*
     * if we allocated data, call init routine
     */
    if (!(rowreq_ctx->rowreq_flags & MFD_ROW_DATA_FROM_USER)) {
        if(SNMPERR_SUCCESS !=
            powerTable_rowreq_ctx_init(rowreq_ctx, user_init_ctx)) {
           powerTable_release_rowreq_ctx(rowreq_ctx);
           rowreq_ctx = NULL;
        }
    }

    return rowreq_ctx;
} /* powerTable_allocate_rowreq_ctx */

/*
 * @internal
 * release resources for a powerTable_rowreq_ctx
 */
void
powerTable_release_rowreq_ctx(powerTable_rowreq_ctx *rowreq_ctx)
{
    DEBUGMSGTL(("internal:powerTable:powerTable_release_rowreq_ctx","called\n"));

    netsnmp_assert(NULL != rowreq_ctx);
    
    powerTable_rowreq_ctx_cleanup(rowreq_ctx);

    if(rowreq_ctx->undo)
        powerTable_release_data(rowreq_ctx->undo);
 
    /*
     * free index oid pointer
     */
    if(rowreq_ctx->oid_idx.oids != rowreq_ctx->oid_tmp)
        free(rowreq_ctx->oid_idx.oids);

    SNMP_FREE(rowreq_ctx);
} /* powerTable_release_rowreq_ctx */

